            if ((iThis >= (int)m_dwNum) || (iThis < 0))
               break;   // beyond the edge of this sentence

           
            // see what testing against
            int iTest;
            if (dwForward)
               iTest = (int)pSweep->dwOffset + (int)dwDistance + 1;
