fp CMTTS::AvgPitchGet (BOOL fFuji)
{
   if (m_lPCMTTSSubVoice.Num()) {
      // ask average pitch with sub-voices
      PCMTTSSubVoice *ppsv = (PCMTTSSubVoice*) m_lPCMTTSSubVoice.Get(0);
      fp fPitch = ppsv[0]->m_fAvgPitch;
      if (fFuji)
         fPitch = fPitch / m_fAvgPitch * m_fAvgPitchF0Fuji; // guess
      return fPitch;
   }

   return fFuji ? m_fAvgPitchF0Fuji : m_fAvgPitch;
}

