            pTTS->FillInVOICEMOD (&vm, pVF, apTTSProsody); // so uses current mod
            pTTS->SynthGenWave (pTTSMP->pWave, pTTSMP->pWave->m_dwSamplesPerSec, szTemp, FALSE, 1 /*iTTSQuality*/, TRUE /* fDisablePCM */, &Progress, &vm);
            pTTSMP->pWave->QuickPlay();

